<script src="https://cdnjs.cloudflare.com/ajax/libs/web3/1.7.4-rc.1/web3.min.js"></script>
<script>
/* To connect using MetaMask */

async function connect() {
    try {
        console.log('Connecting wallet...');
        if (window.ethereum) {
            // Request account access
            await ethereum.request({ method: 'eth_requestAccounts' });
            console.log('Wallet connected:', web3.eth.defaultAccount);
            // You are now connected and can interact with the smart contract
        } else {
            console.error('MetaMask not detected. Please install MetaMask.');
        }
    } catch (error) {
        console.error('Wallet connection error:', error.message);
    }
}

// Import the Web3 library
const Web3 = require('web3');

// Connect to the blockchain network
const web3 = new Web3(new Web3.providers.HttpProvider('http://localhost:8545'));

// Get the contract ABI (Application Binary Interface)
const contractABI = [
  {
    constant: false,
    inputs: [
      {
        name: '_message',
        type: 'string',
      },
    ],
    name: 'setMessage',
    outputs: [],
    payable: false,
    stateMutability: 'nonpayable',
    type: 'function',
  },
  {
    constant: true,
    inputs: [],
    name: 'getMessage',
    outputs: [
      {
        name: '',
        type: 'string',
      },
    ],
    payable: false,
    stateMutability: 'view',
    type: 'function',
  },
];

// Get the contract address
const contractAddress = '0x...';

// Create an instance of the contract
const contract = new web3.eth.Contract(contractABI, contractAddress);

// Call the getMessage function
contract.methods.getMessage().call((error, result) => {
  console.log(result);
});

// Call the setMessage function
contract.methods.setMessage('Hello, world!').send({ from: '0x...' }, (error, transactionHash) => {
  console.log(transactionHash);
});